mapper.select:

select * from
	(select no, image, title, content, regdate, readcount, rownum r 
		from(select * from board order by regdate desc) 
	order by regdate desc) 
where r >= #{start} AND r <= #{end}

select * from (select no, image, title, content, regdate, readcount, rownum r from (select * from fstory
title like '%'||#{search}||'%' content like '%'||#{search}||'%' id like '%'||#{search}||'%'
order by regdate desc) order by regdate desc ) where r >= #{start} AND r <= #{end}
