mapper.select:

select * from
(select no, image, title, content, regdate, readcount, id, stitle, rownum r
from (select * from mpick order by regdate desc) 



      title like '%'||#{search}||'%'
       content like '%'||#{search}||'%' 
       id like '%'||#{search}||'%' 


order by regdate desc 

) where r >= #{start} AND r <= #{end}
