mapper.select:

select * from
(select no, name, category, address, phone, menu, detail, map_lt, map_ln, hashtag, rownum r		
from (select * from store order by no desc) 


		 name like '%'||#{search}||'%'
		 category like '%'||#{search}||'%' 


order by no desc 

) where r >= #{start} AND r <= #{end}
